    vector<int> opt;
    for (int i = 0; i < n; i++) {
      int lo = 0, hi = opt.size();
      while (lo < hi) {
        int mid = lo + (hi - lo) / 2;
        if (opt[mid] < nums[i])
          lo = mid + 1;
        else
