      for (int j = 0; j < m; j++) {
        int len = wordDict[j].length();
        if (len <= i && opt[i - len] &&
            dict.count(s.substr(i - 1 - len + 1, len))) {
          opt[i] = true;
          break;
        }
      }
    }
    return opt[n];
  }
};
